import React from 'react'
import { Link } from 'react-router-dom';
import UserService from '../service/UserService';
function LandingPage() {

  const buttonStyle = {
    margin: '10px',
    padding: '10px 20px',
    height: '10vh',
    width: '200px',
    backgroundColor: 'black',
    border: '1px solid #ccc',
    borderRadius: '5px',
    cursor: 'pointer',
  };
  const handleLogout = () => {
        const confirmDelete = window.confirm('Are you sure you want to logout this user?');
        if (confirmDelete) {
            UserService.logout();
        }
    };
      const [isDownloading, setIsDownloading] = useState(false);
    const handleDownloadExcel = async () => {
    setIsDownloading(true);
    try {
      const response = await fetch(`${UserService.BASE_URL}/export/excel`, {
        method: 'POST',
      });

      if (!response.ok) {
        throw new Error('Failed to generate Excel file');
      }

      const blob = await response.blob();
      const url = URL.createObjectURL(blob);

      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'data.xls'); // Change extension for desired format
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (error) {
      console.error('Error downloading Excel:', error);
    } finally {
      setIsDownloading(false);
    }
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '75vh' }}>
      <div>
        <button style={buttonStyle}><Link to={`/user-management`}>View Users</Link></button>
        <button style={buttonStyle}><Link to={`/register`}>Add Users</Link></button>
        <button style={buttonStyle}><Link to={`/user-management`}>Export to excel</Link></button>
        <button style={buttonStyle}><Link to={`/user-management`}>Export to pdf</Link></button>
        <button style={buttonStyle} onClick={handleLogout}>Logout</button>
      </div>
    </div>
  );
};

export default LandingPage
